import java.util.Scanner;

public class Q2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

Scanner keyboard = new Scanner(System.in);
		
		System.out.println("Enter a number: ");
		int x = keyboard.nextInt();
		
		for(int i = x; i>0; i--) {
			int y=i;
			
			while(y>0) {
				System.out.print("*");
				y--;
			}
			System.out.println("");
		}
		

		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
